package com.uchain.core;import com.google.common.collect.Lists;import com.uchain.main.Witness;import lombok.Getter;import lombok.Setter;import java.time.Instant;import java.util.List;@Getter@Setterclass PendingState {    private Witness producer;    private long blockTime;    private long startTime;    List<Transaction> txs = Lists.newArrayList();    public void set(Witness producer, long blockTime) {        this.producer = producer;        this.blockTime = blockTime;        this.startTime = Instant.now().toEpochMilli();    }}