package com.uchain.core.datastore.keyvalue;import com.uchain.common.Serializabler;import lombok.val;import java.io.*;public class StringValue implements Converter<String>{    @Override    public byte[] toBytes(String key) {        val bs = new ByteArrayOutputStream();        val os = new DataOutputStream(bs);        serializer(key,os);        return bs.toByteArray();    }    @Override    public String fromBytes(byte[] bytes) {        val bs = new ByteArrayInputStream(bytes);        val is = new DataInputStream(bs);        return deserializer(is);    }    @Override    public String deserializer(DataInputStream is) {        String str = null;        try {            str = Serializabler.readString(is);        } catch (IOException e) {            e.printStackTrace();        }        return str;    }    @Override    public void serializer(String key, DataOutputStream os) {        try {            Serializabler.writeString(os,key);        } catch (IOException e) {            e.printStackTrace();        }    }}