package com.uchain.main;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.Setter;@Setter@Getter@AllArgsConstructorpublic class SystemProperties {    boolean vmTrace;    int dumpBlock;    String dumpStyle;    boolean recordInternalTransactionsData;    boolean playVM;    boolean vmTraceCompressed;    String solcPath;    boolean eip161 = false;    boolean eip198 = false;    boolean eip213 = false;    boolean eip212 = false;    boolean eip1283 = false;    static SystemProperties systemProperties;    public static SystemProperties getDefault(){        if(systemProperties == null) return new SystemProperties(false, -1, "pretty",                true, true,true,null, false,                false, false, false, false);        return systemProperties;    }    public static void setDefault(SystemProperties config){        systemProperties = config;    }}