package com.uchain.storage;import com.google.common.collect.Maps;import lombok.Getter;import lombok.Setter;import java.util.ArrayList;import java.util.List;import java.util.Map;@Getter@Setterpublic class Batch {    private List<BatchItem> ops = new ArrayList();    public Batch put(byte[] key,byte[] value){        ops.add(new PutOperationItem(key,value));        return this;    }    public Batch delete(byte[] key){        ops.add(new DeleteOperationItem(key));        return this;    }}