package com.uchain.vm;public class MessageCall {    private final OpCode type;    private final DataWord gas;    private final DataWord codeAddress;    private final DataWord endowment;    private final DataWord inDataOffs;    private final DataWord inDataSize;    private DataWord outDataOffs;    private DataWord outDataSize;    public MessageCall(OpCode type, DataWord gas, DataWord codeAddress,                       DataWord endowment, DataWord inDataOffs, DataWord inDataSize) {        this.type = type;        this.gas = gas;        this.codeAddress = codeAddress;        this.endowment = endowment;        this.inDataOffs = inDataOffs;        this.inDataSize = inDataSize;    }    public MessageCall(OpCode type, DataWord gas, DataWord codeAddress,                       DataWord endowment, DataWord inDataOffs, DataWord inDataSize,                       DataWord outDataOffs, DataWord outDataSize) {        this(type, gas, codeAddress, endowment, inDataOffs, inDataSize);        this.outDataOffs = outDataOffs;        this.outDataSize = outDataSize;    }    public OpCode getType() {        return type;    }    public DataWord getGas() {        return gas;    }    public DataWord getCodeAddress() {        return codeAddress;    }    public DataWord getEndowment() {        return endowment;    }    public DataWord getInDataOffs() {        return inDataOffs;    }    public DataWord getInDataSize() {        return inDataSize;    }    public DataWord getOutDataOffs() {        return outDataOffs;    }    public DataWord getOutDataSize() {        return outDataSize;    }}